import { FC } from "react";

type Props = {
  title: string;
};
export const Modal: FC<Props> = ({ children, title }) => {
  return (
    <>
      <div className="modal-bg">
        <div className="modal">
          <div className="modal-title">{title}</div>
          {children}
        </div>
      </div>
      <style jsx>{`
        .modal-bg {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          z-index: 999;
          background: rgba(0, 0, 0, 0.8);
          display: flex;
          justify-content: center;
        }
        .modal {
          background: red;
          align-self: center;
          background: #fff;
          border-radius: 3px;
          padding: 18px;
        }
      `}</style>
    </>
  );
};
