import { FC, useReducer, ChangeEvent } from "react";
import { Layout } from "../components/layout/layout";
import { GfycatPlayerContrainer } from "../components/nadepage/GfycatPlayerContainer";
import { Button, Input, Segment, Grid, Divider } from "semantic-ui-react";
import { ImageUploader } from "../components/ImageUploader";
import { NewNadeGfycatPlaceholder } from "../components/newnade/NewNadeGfycatPlaceholder";
import { NewNadeImagePlaceholder } from "../components/newnade/NewNadeImagePlaceholder";

type Steps = "intro" | "gfycat" | "image";

type NewPageState = {
  step: Steps;
  gfycatID?: string;
};

type Actions =
  | {
      type: "step";
      payload: Steps;
    }
  | { type: "setGfycatID"; payload: string };

const NewNadePage: FC = () => {
  const initialState: NewPageState = {
    step: "intro"
  };

  function reducer(state: NewPageState, action: Actions): NewPageState {
    switch (action.type) {
      case "step":
        return {
          ...state,
          step: action.payload
        };
      case "setGfycatID":
        return {
          ...state,
          gfycatID: action.payload
        };
      default:
        return state;
    }
  }

  const [state, dispatch] = useReducer(reducer, initialState);

  const { step } = state;

  function onGyfcatInputChange(e: ChangeEvent<HTMLInputElement>) {
    dispatch({ type: "setGfycatID", payload: e.target.value });
  }

  function onGfycatSave() {
    console.log("Shouldvalidate");
    nextStep();
  }

  function nextStep() {
    switch (state.step) {
      case "intro":
        dispatch({ type: "step", payload: "gfycat" });
        break;
      case "gfycat":
        dispatch({ type: "step", payload: "image" });
    }
  }

  return (
    <Layout>
      <div className="nade-new-container">
        <Segment placeholder>
          <Grid columns={2} stackable textAlign="center">
            <Divider vertical>And</Divider>

            <Grid.Row verticalAlign="middle">
              <Grid.Column color="grey">
                <NewNadeGfycatPlaceholder />
              </Grid.Column>

              <Grid.Column>
                <NewNadeImagePlaceholder />
              </Grid.Column>
            </Grid.Row>
          </Grid>
        </Segment>
      </div>
      <style jsx>
        {`
          .nade-new-container {
            margin: 18px;
          }
        `}
      </style>
    </Layout>
  );

  return (
    <Layout>
      <div className="nade-new-container">
        {step === "intro" && (
          <div className="nade-new-step-intro">
            <div className="nade-new-explenation">
              <h2>Add new nade</h2>
              <h3>Requirements</h3>
              <ul>
                <li>Gfycat video of you performing the throw</li>
                <ul>
                  <li>
                    Use <code>cl_draw_only_deathnotices 1</code> to remove your
                    HUD when recording.
                  </li>
                </ul>
                <li>Image of the result of the trow</li>
              </ul>
              <Button primary onClick={nextStep}>
                Let's go!
              </Button>
            </div>
            <div className="nade-new-examples">
              <h3>Example gfycat video</h3>
              <GfycatPlayerContrainer
                disableEdit
                gfyID="IdioticAdventurousArabianoryx"
                aspect="16:10"
              />
              <h3>Example result image</h3>
              <img
                width="100%"
                src="http://s3-eu-central-1.amazonaws.com/csgonades/nades/result_images/000/000/319/large/smoke.jpg?1507738073"
              />
            </div>
          </div>
        )}
        {step === "gfycat" && (
          <div className="nade-new-step-gfycat">
            <div className="nade-new-explenation">
              <h2>Gfycat</h2>
              Let's add your gfycat video!
              <Input
                onChange={onGyfcatInputChange}
                icon={{
                  name: "check",
                  circular: true,
                  link: true,
                  onClick: onGfycatSave
                }}
                placeholder="Gfrycat url or id..."
              />
            </div>
            <div className="nade-new-action">
              <GfycatPlayerContrainer disableEdit gfyID={state.gfycatID} />
            </div>
          </div>
        )}
        {step === "image" && (
          <div className="nade-new-step-image">
            <div className="nade-new-explenation">
              <h2>Result image</h2>
              Let's add your result image!
            </div>
            <div className="nade-new-action">
              <ImageUploader />
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export { NewNadePage };
