import { CSGNModal } from "../CSGNModal";
import { Input, Button } from "semantic-ui-react";
import { useState } from "react";

type Props = {
  visible: boolean;
  onDismiss: () => void;
};

export const NewNadeGfycatModal = ({ onDismiss, visible }: Props) => {
  const [isLoading] = useState(false);
  const [gfycatValue, setGfycatValue] = useState("");
  return (
    <>
      <CSGNModal
        visible={visible}
        onDismiss={onDismiss}
        title="Add gfycat video"
      >
        <Input
          loading={isLoading}
          placeholder="Gfycat url or id"
          value={gfycatValue}
          onChange={e => {
            setGfycatValue(e.target.value);
          }}
        />
        <Button>Add</Button>
      </CSGNModal>
    </>
  );
};
